SELECT r.RESTAURANT_ID, r.RESTAURANT_NAME, r.RESTAURANT_ADDRESS, r.RESTAURANT_RESERVATIONS, r.RESTAURANT_WIFI, r.RESTAURANT_DELIVERY, r.RESTAURANT_MULTIBANCO, r.RESTAURANT_OUTDOOR_SEATING, r.RESTAURANT_POINTS, r.RESTAURANT_IMAGE, c.LATITUDE, c.longitude FROM RESTAURANTS r JOIN COORDS c ON r.RESTAURANT_ID = c.RESTAURANT_ID WHERE r.RESTAURANT_ID = '6'